```

This is the UI Layer :

```
    public interface INodeModel<T> : INodeModel
    where T : struct
    {
        new T? ID { get; set; }
        new T? ParentID { get; set; }
        new ObservableCollection<INodeModel<T>> Children { get; }
    }

    public interface INodeModel
    {
        object ID { get; set; }
        object ParentID { get; set; }
        string Name { get; set; }
        bool IsSelected { get; set; }
        bool IsExpanded { get; set; }
        IEnumerable<INodeModel> Children { get; }
    }

    public class NodeModel<T> : INodeModel<T>
     where T : struct
    {
        public T? ID { get; set; }
        public T? ParentID { get; set; }
        public string Name { get; set; }
        public bool IsSelected { get; set; }
        public bool IsExpanded { get; set; }
        public ObservableCollection<INodeModel<T>> Children { get; set; }

        object INodeModel.ID
        {
            get
            {
                return ID;
            }

            set
            {
                ID = value as T?;
            }
        }

        IEnumerable<INodeModel> INodeModel.Children
        {
            get
            {
                return Children;
            }
        }

        object INodeModel.ParentID
        {
            get
            {
                return ParentID;
            }

            set
            {
                ParentID = value as T?;
            }
        }
    }

    public class Branch
    {
        public int? ID { get; set; }
        public string Name { get; set; }

        // It has to be 'IEnumerable<INodeModel<short>>' because just the first time that the branch gets form Serivce Layer the type of T is OrganizationDto ('IEnumerable<OrganizationDto>'),
        // Other times ( for example wheneever i try to load a node children) it's NodeDto<T> or NodeDto<short> So it has to be polymorphic =>  INodeModel<short>             
        public IEnumerable<INodeModel<short>> Organizations { get; set; }
    }

```

