Here is solution (additional code for AutoMapper.QueryableExtensions.Extensions class):

```
public static class Extensions
{
    private static readonly MethodInfo s_queryableSelectMethod = FindQueryableSelectMethod();

    public static IQueryable<TResult> ProjectTo<TResult>( this IQueryable source )
    {
        return source.ProjectTo<TResult>(Mapper.Engine);
    }

    public static IQueryable<TResult> ProjectTo<TResult>( this IQueryable source, IMappingEngine mappingEngine )
    {
        var expr = _expressionCache.GetOrAdd(new TypePair(source.ElementType, typeof(TResult)), tp => CreateMapExpression(mappingEngine, tp, DictionaryFactory.CreateDictionary<TypePair, int>()));
        return source.Provider.CreateQuery<TResult>(
            Expression.Call(
                null,
                s_queryableSelectMethod.MakeGenericMethod(source.ElementType, typeof(TResult)),
                new[] { source.Expression, Expression.Quote(expr) }
            )
        );
    }

    private static MethodInfo FindQueryableSelectMethod()
    {
        return (
            from candidate in typeof(Queryable).GetMethods(BindingFlags.Static | BindingFlags.Public)
            where candidate.Name == "Select"
            let parameters = candidate.GetParameters()
            where parameters.Length == 2
            where parameters[0].ParameterType.IsGenericType && parameters[0].ParameterType.GetGenericTypeDefinition() == typeof(IQueryable<>)
            where parameters[1].ParameterType.IsGenericType && parameters[1].ParameterType.GetGenericTypeDefinition() == typeof(Expression<>)
            let parameter1args = parameters[1].ParameterType.GetGenericArguments()
            where parameter1args[0].IsGenericType && parameter1args[0].GetGenericTypeDefinition() == typeof(Func<,>)
            let parameter1args0args = parameter1args[0].GetGenericArguments()
            where parameter1args0args[0].IsGenericParameter && parameter1args0args[1].IsGenericParameter
            select candidate
        ).First();
    }
}
