        using(var context = new TestContext())
        {
            var book1 = context.Books.Where(b => b.Id == 1).First();
            book1.Pages.Add(new Page());
            await context.SaveChangesAsync();

            var book2 = context.Books.Where(b => b.Id == 2).First();
            book2.Pages.Add(new Page());
            await context.SaveChangesAsync();
        }
