await using var context = new CustomerContext();
await context.Database.EnsureDeletedAsync();
await context.Database.EnsureCreatedAsync();

var phoneNumber = new PhoneNumber(44, 7777555777);
var customersWithNumber = await context.Customers.Where(e => e.MobilePhone == phoneNumber).ToListAsync();

public class CustomerContext : DbContext
{
    public DbSet<Customer> Customers => Set<Customer>();

    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)
        => optionsBuilder.UseSqlServer(@"Server=(localdb)\mssqllocaldb;Database=AllTogetherNow")
            .EnableSensitiveDataLogging()
            .LogTo(Console.WriteLine, LogLevel.Information);

    protected override void OnModelCreating(ModelBuilder modelBuilder)
    {
        modelBuilder.Entity<Customer>(
            b =>
            {
                b.ComplexProperty(e => e.HomePhone);
                b.ComplexProperty(e => e.WorkPhone);
                b.ComplexProperty(e => e.MobilePhone);
            });
    }
}

public class Customer
{
    public int Id { get; set; }
    public string Name { get; set; } = null!;
    public PhoneNumber HomePhone { get; init; }
    public PhoneNumber WorkPhone { get; init; }
    public PhoneNumber MobilePhone { get; init; }
}

public readonly record struct PhoneNumber(int CountryCode, long Number);
