// <auto-generated> This file has been auto generated by EF Core Power Tools. </auto-generated>
#nullable disable
using System;
using System.Collections.Generic;
using Microsoft.EntityFrameworkCore;

namespace DB.Models;

public partial class TestContext : DbContext
{
    public TestContext(DbContextOptions<TestContext> options)
        : base(options)
    {
    }

    public virtual DbSet<Join> Joins { get; set; }

    public virtual DbSet<List> Lists { get; set; }

    protected override void OnModelCreating(ModelBuilder modelBuilder)
    {
        modelBuilder.Entity<Join>(entity =>
        {
            entity.HasKey(e => new { e.TargetId, e.SourceId, e.PrimaryGroup }).HasName("PK_Way");

            entity.ToTable("Joins", "Testing");

            entity.Property(e => e.PrimaryGroup).HasDefaultValue(true);
        });

        modelBuilder.Entity<List>(entity =>
        {
            entity.HasKey(e => e.Id).HasName("PK_TestingList");

            entity.ToTable("List", "Testing");

            entity.Property(e => e.Id).HasDefaultValueSql("(newsequentialid())");
        });

        OnModelCreatingPartial(modelBuilder);
    }

    partial void OnModelCreatingPartial(ModelBuilder modelBuilder);
}

