using (var context = new AppDbContext())
{
    await context.Schools
        .SelectMany(e => e.OpeningHours)
        .Where(e => e.DayOfWeek == DayOfWeek.Friday)
        .ExecuteUpdateAsync(s => s.SetProperty(t => t.OpensAt, t => t.OpensAt!.Value.AddHours(-1)));
