body:
Updated TestContext.cs to illustrate the 2 problems (see my `// NOTE` comments in code).
1. When mapping a JSON Object (`IntercompanySchedule.CustomCostings`), you must provide your own `.HasConversion` if you want CompiledModels.
1. When mapping a JSON Array (`WorkOrder.CustomProperties`), when you provide `.HasConversion` [per guidance](https://learn.microsoft.com/en-us/ef/core/modeling/value-comparers?tabs=ef5), use `JsonSerializerOptions.Default` instead of `(JsonSerializerOptions)null` which at least produces a compiler warning (CS8600) depending on your project setup (`<Nullable>enable</Nullable>`).

## TestContext.cs
```C#
using System.Text.Json;
using Microsoft.EntityFrameworkCore;
using Microsoft.EntityFrameworkCore.ChangeTracking;
using Microsoft.EntityFrameworkCore.Metadata.Builders;
using Microsoft.EntityFrameworkCore.Storage.ValueConversion;

namespace TestContext
{
    #region Model using JSON Object

    public class ScheduleCosting
    {
        public int Id { get; set; }
        public DateTime StartDateLocal { get; set; }
    }

    public class CustomCostings
    {
        public List<ScheduleCosting> ScheduleCostings { get; set; } = [];
    }

    public class IntercompanySchedule
    {
        public int Id { get; set; }
        public CustomCostings CustomCostings { get; set; } = new();
    }

    #endregion

    #region Mapping using JSON Object

    public class IntercompanyScheduleMap : IEntityTypeConfiguration<IntercompanySchedule>
    {
        public void Configure(EntityTypeBuilder<IntercompanySchedule> builder)
        {
            builder.ToTable("intercompany_schedule");
            builder.Property(t => t.Id).HasColumnName("intercompany_schedule_id");

            // NOTE not calling .HasConversion here, so CompiledModels generate incorrectly
            builder.Property(t => t.CustomCostings).HasColumnName("custom_costings")
                .HasColumnType("json")
            ;
        }
    }

    #endregion

    #region Model using JSON Array

    public class CustomProperty
    {
        public string Key { get; set; } = "";
        public string Value { get; set; } = "";
    }

    public class WorkOrder
    {
        public int Id { get; set; }
        public ICollection<CustomProperty>? CustomProperties { get; set; } = [];
    }

    #endregion

    #region Mapping using JSON Array

    public class WorkOrderMap : IEntityTypeConfiguration<WorkOrder>
    {
        public void Configure(EntityTypeBuilder<WorkOrder> builder)
        {
            builder.ToTable("t_work_order");
            builder.Property(t => t.Id).HasColumnName("work_order_id");
            builder.Property(t => t.CustomProperties).HasColumnName("custom_properties")
                .HasColumnType("json")
                .HasConversion<WorkOrderCustomPropertiesConverter, WorkOrderCustomPropertiesComparer>()
            ;
        }
    }

    // via https://stackoverflow.com/a/73719507/158502
    internal class WorkOrderCustomPropertiesConverter : ValueConverter<ICollection<CustomProperty>?, string>
    {
        // NOTE not using JsonSerializerOptions.Default here, so CompiledModels generate incorrectly
        public WorkOrderCustomPropertiesConverter()
            : base(v => JsonSerializer.Serialize(v, (JsonSerializerOptions)null), // NOTE
             v => JsonSerializer.Deserialize<ICollection<CustomProperty>?>(v, (JsonSerializerOptions)null),
             null)
        { }
    }

    // via https://stackoverflow.com/a/73719507/158502
    internal class WorkOrderCustomPropertiesComparer : ValueComparer<ICollection<CustomProperty>?>
    {
        public WorkOrderCustomPropertiesComparer()
            : base((c1, c2) => c1.SequenceEqual(c2),
                    c => c.Aggregate(0, (a, v) => HashCode.Combine(a, v.GetHashCode())),
                    c => c.ToHashSet())
        { }
    }

    #endregion Mapping

    #region Context

    public class TestContext : DbContext
    {
        private readonly string? _connectionString = "port=3306";

        public TestContext() { }

        public TestContext(string connectionString)
        {
            _connectionString = connectionString;
        }

        protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)
        {
            optionsBuilder.UseMySql(
                _connectionString,
                new MySqlServerVersion("8.0.21"),
                mySqlOptions => mySqlOptions
                    .UseMicrosoftJson(MySqlCommonJsonChangeTrackingOptions.FullHierarchyOptimizedSemantically)
            );
        }

        protected override void OnModelCreating(ModelBuilder modelBuilder)
        {
            base.OnModelCreating(modelBuilder);

            modelBuilder.ApplyConfiguration(new IntercompanyScheduleMap());
            modelBuilder.ApplyConfiguration(new WorkOrderMap());
        }

        public DbSet<IntercompanySchedule> IntercompanySchedules { get; set; }
        public DbSet<WorkOrder> WorkOrders { get; set; }
    }

    #endregion Context
}
```
