    // Create RuleTester instance
    const ruleTester = new RuleTester();

    // Attempt to trigger serialization check via RuleTester.run
    // RuleTester internally uses isSerializable to check test cases
    ruleTester.run('mock-rule', mockRule, {
      valid: [
        {
          code: 'var x = 1;',
          options: [circularRef]
        }
      ],
      invalid: []
    });
  } catch (error) {
    console.log('Attack result:');
    console.log(error.message);
    console.log(error.stack.split('\n').slice(0, 8).join('\n'));

    if (error.message.includes('call stack size exceeded')) {
