func (o *InsertRecordsParams) validateRecordsBody(formats strfmt.Registry) error {

	recordsSize := int64(len(o.Records))

	// maxItems: 10000
	if err := validate.MaxItems("records", "body", recordsSize, 10000); err != nil {
		return err
	}

	recordsIC := o.Records

	var recordsIR []interface{}
	for i, recordsIV := range recordsIC {
		recordsI := recordsIV
		if err := recordsI.Validate(formats); err != nil {
			if ve, ok := err.(*errors.Validation); ok {
				return ve.ValidateName(fmt.Sprintf("%s.%v", "records", i))
			}
			return err
		}

		recordsIR = append(recordsIR, recordsI)
	}

	o.Records = recordsIR
	return nil
}
