  @MethodSource("createElementArguments")
  @ParameterizedTest(name = "Do Something With Element {0} and expect {2}")
  void testArguments(
      String test,
      MyElemment element,
      int expectedCode) {
    given()
        .request()
          .body(element)
        .then()
          .expect()
          .statusCode(expectedCode)
        .request()
        .put("/path/to/put");
