@JsonSerialize(converter=Request.Converter.class)
public class Request {
    public int x() {
        return 1;
    }
    private static class Converter extends StdConverter<Request, Map<String,Object>> {
        @SuppressWarnings("unused")
        public Converter() {}
        @Override
        public Map<String,Object> convert(final Request value) {
            final Map<String, Object> test = new LinkedHashMap<String, Object>(1);
            final Map<String, Object> innerTest = new LinkedHashMap<String, Object>(1);
            innerTest.put("value", value.x());
            test.put("hello", innerTest);
            return test;
        }
    }
}
