@Test
public void checkConverter() throws JsonProcessingException {
    final ObjectMapper mapper = new ObjectMapper();
    final Request request = new Request();
    // will throw exception
    // com.fasterxml.jackson.databind.JsonMappingException: No serializer found for class java.lang.String and no properties discovered to create BeanSerializer (to avoid exception, disable SerializationFeature.FAIL_ON_EMPTY_BEANS) ) (through reference chain: java.util.LinkedHashMap["value"])
    // yes I disabled failure on empty bean --> {"hello":{}}
    final String retString = mapper.writeValueAsString(request);
    assertEquals("{\"hello\":{\"value\":1}}",retString);
}
