@Test
public void checkWriter() throws JsonProcessingException {
    final ObjectMapper mapper = new ObjectMapper();
    final Map<String, Object> test = new LinkedHashMap<String, Object>(1);
    final Map<String, Object> innerTest = new LinkedHashMap<String, Object>(1);
    innerTest.put("value", 1);
    test.put("hello", innerTest);
    final String retString = mapper.writeValueAsString(test);
    // correct {"hello":{"value":1}}
    assertEquals("{\"hello\":{\"value\":1}}",retString);
}
