@JsonSerialize(include = Inclusion.NON_NULL)
public class ActivityEntity implements Serializable {

    @Nullable
    private String id;

    @Nullable
    private String type;

    @Nullable
    private String status;

    @Nullable
    private String context;

    @JsonCreator
    public ActivityEntity(@JsonProperty("id") final String id, @JsonProperty("type") final String type) {
        this.id = id;
        this.type = type;
    }
}
