@ApiModel(description = "@v1.0.sftpconnectionbean.apimodel.description", value = "SFTP Connnection")
public class SFTPConnectionBean {
    @ApiModelProperty(required = false, value = "@v1.0.sftpconnectionbean.apimodelproperty.hostname.description")
    private String hostName;

    @ApiModelProperty(required = false, value = "@v1.0.sftpconnectionbean.apimodelproperty.hostport.description")
    private Long hostPort;

    @ApiModelProperty(required = false, value = "@v1.0.sftpconnectionbean.apimodelproperty.username.description")
    private String userName;

    @ApiModelProperty(required = false, value = "@v1.0.sftpconnectionbean.apimodelproperty.password.description")
    private String password;

    @ApiModelProperty(required = false, value = "@v1.0.sftpconnectionbean.apimodelproperty.destinationdir.description")
	private String destinationDir;

	/**
	 * @return the hostName
	 */
	public String getHostName() {
		return hostName;
	}

