	 * @param hostName
	 *            the hostName to set
	 */
	public void setHostName(String hostName) {
		this.hostName = hostName;
	}

	/**
	 * @return the hostPort
	 */
	public Long getHostPort() {
		return hostPort;
	}

	/**
	 * @param hostPort
	 *            the hostPort to set
	 */
	public void setHostPort(Long hostPort) {
		this.hostPort = hostPort;
	}

	/**
	 * @return the userName
	 */
	public String getUserName() {
		return userName;
	}

	/**
	 * @param userName
	 *            the userName to set
	 */
	public void setUserName(String userName) {
		this.userName = userName;
	}

	/**
	 * @return the password
	 */
	public String getPassword() {
		return password;
	}

	/**
	 * @param password
	 *            the password to set
	 */
	public void setPassWord(String password) {
		this.password = password;
	}

	public String getDestinationDir() {
		return destinationDir;
	}

	public void setDestinationDir(String destinationDir) {
		this.destinationDir = destinationDir;
	}

	/*
	 * (non-Javadoc)
	 * 
	 * @see java.lang.Object#hashCode()
	 */
	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result
				+ ((hostName == null) ? 0 : hostName.hashCode());
		result = prime * result
				+ ((hostPort == null) ? 0 : hostPort.hashCode());
		result = prime * result
				+ ((userName == null) ? 0 : userName.hashCode());
		return result;
	}

	/*
	 * (non-Javadoc)
	 * 
	 * @see java.lang.Object#equals(java.lang.Object)
	 */
	@Override
	public boolean equals(Object obj) {
		if (obj == null || getClass() != obj.getClass())
			return false;
		if (hostName == null || hostPort == null || userName == null)
			return false;
		SFTPConnectionBean other = (SFTPConnectionBean) obj;
		return checkEquals(other);
	}

	/**
	 * @param other
	 */
	private boolean checkEquals(SFTPConnectionBean other) {
		return hostName.equalsIgnoreCase(other.hostName)
				&& hostPort.equals(other.hostPort)
				&& userName.equals(other.userName);
	}

}

