import static com.fasterxml.jackson.annotation.JsonTypeInfo.As.WRAPPER_ARRAY;
import com.fasterxml.jackson.databind.JsonMappingException;
import com.fasterxml.jackson.databind.ObjectMapper;
import static com.fasterxml.jackson.databind.ObjectMapper.DefaultTyping.OBJECT_AND_NON_CONCRETE;
import java.util.Objects;
import static org.junit.Assert.*;
import org.junit.Test;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class TestDefaultTyping
{
    private static final Logger LOGGER = LoggerFactory.getLogger(TestDefaultTyping.class);

    @Test
    public void testPojo() throws Exception
    {
        ObjectMapper mapper = new ObjectMapper();
        mapper.enableDefaultTyping(OBJECT_AND_NON_CONCRETE, WRAPPER_ARRAY);

        Object expected = new Foo(new Foo("bar"));
        String json = mapper.writerFor(Object.class).writeValueAsString(expected);
        LOGGER.debug("json = {}", json);

        Object actual = mapper.readerFor(Object.class).readValue(json); //FIXME it fails
        assertEquals(expected, actual);

        try
        {
            mapper.readerFor(Foo.class).readValue(json);
            fail(); //FIXME it does not fail
        } catch (JsonMappingException ex)
        {
            LOGGER.debug("Must Fail", ex);
        }
    }

    @Test
    public void testArray() throws Exception
    {
        ObjectMapper mapper = new ObjectMapper();
        mapper.enableDefaultTyping(OBJECT_AND_NON_CONCRETE, WRAPPER_ARRAY);

        Object[] expected = new Foo[]
        {
            new Foo(new Foo("bar"))
        };
        String json = mapper.writerFor(Object[].class).writeValueAsString(expected);
        LOGGER.debug("json = {}", json);

        Object[] actual = mapper.readerFor(Object[].class).readValue(json);
        assertArrayEquals(expected, actual);

        try
        {
            mapper.readerFor(Foo[].class).readValue(json);
            fail();
        } catch (JsonMappingException ex)
        {
            LOGGER.debug("Must Fail", ex);
        }
    }

    public static class Foo
    {
        private Object value;

        public Foo()
        {
        }

        public Foo(Object value)
        {
            this.value = value;
        }

        public Object getValue()
        {
            return value;
        }

        public void setValue(Object value)
        {
            this.value = value;
        }

        @Override
        public int hashCode()
        {
            int hash = 7;
            hash = 53 * hash + Objects.hashCode(this.value);
            return hash;
        }

        @Override
        public boolean equals(Object obj)
        {
            if (obj == null)
            {
                return false;
            }
            if (getClass() != obj.getClass())
            {
                return false;
            }
            final Foo other = (Foo) obj;
            return Objects.equals(this.value, other.value);
        }

    }
}
