        @JsonCreator
        public static Operation forValue(final String idStr) {
            Operation candidate = mapping.get(Long.parseLong(idStr));
            if (candidate == null) {
                throw new IllegalArgumentException("Unable " + idStr);
            }
            return candidate;
        }

        @JsonValue
        public long toValue() {
            return id;
        }
    }
}
