``` 
git show bb452d1
commit bb452d1091d30df5dd651460b77f7af847941dc5
Author: uppfinnarn <uppfinnarn@gmail.com>
Date:   Fri Mar 10 15:56:25 2017 +0100

    Quick refactor

diff --git a/lib/engine.go b/lib/engine.go
index 85dcd6e..0e10620 100644
--- a/lib/engine.go
+++ b/lib/engine.go
@@ -617,6 +617,9 @@ func (e *Engine) runVUOnce(ctx context.Context, vu *vuEntry) bool {
        }
 
        t := time.Now()
+
+       atomic.AddInt64(&vu.Iterations, 1)
+       atomic.AddInt64(&e.numIterations, 1)
        samples = append(samples,
                stats.Sample{
                        Time:   t,
@@ -637,19 +640,14 @@ func (e *Engine) runVUOnce(ctx context.Context, vu *vuEntry) bool {
                                Value:  1,
                        },
                )
+               atomic.AddInt64(&e.numErrors, 1)
        }
 
        vu.lock.Lock()
        vu.Samples = append(vu.Samples, samples...)
        vu.lock.Unlock()
 
-       atomic.AddInt64(&vu.Iterations, 1)
-       atomic.AddInt64(&e.numIterations, 1)
-       if err != nil {
-               atomic.AddInt64(&e.numErrors, 1)
-               return false
-       }
-       return true
+       return err != nil
 }
 
