```
public static class BarebonesStage extends ApplicationAdapter {
        Stage stage;

        public void create () {
            stage = new Stage(new ScreenViewport());
            Gdx.input.setInputProcessor(stage);

            Skin skin = new Skin(Gdx.files.internal("data/uiskin.json"));

            TextArea textArea = new TextArea("Text", skin);

            Table table = new Table();
            stage.addActor(table);
            table.setFillParent(true);

            table.debug();
            table.defaults().space(6);
            table.add(textArea);
        }

        public void render () {
            Gdx.gl.glClear(GL20.GL_COLOR_BUFFER_BIT);
            stage.draw();
        }

        public void resize (int width, int height) {
            // Pass false to not modify the camera position.
            stage.getViewport().update(width, height, true);
        }

        public static void main (String[] args) throws Exception {
            new LwjglApplication(new BarebonesStage());
        }
    }
