  @Test
  public void testLazyX509Certificate() throws Exception {
    CertificateFactory certificateFactory = CertificateFactory.getInstance("X.509");
    X509Certificate x509Certificate =
      (X509Certificate) certificateFactory.generateCertificate(
        new ByteArrayInputStream(CERTIFICATE.getBytes(StandardCharsets.UTF_8))
      );
    LazyX509Certificate lazyX509Certificate = new LazyX509Certificate(CERTIFICATE.getBytes(StandardCharsets.UTF_8));

    assertThat(x509Certificate.getIssuerAlternativeNames()).isNotNull();
    assertThat(x509Certificate.getIssuerAlternativeNames().toString()).contains("www.foo.com");

