                    await SearchClient.Indexes.DeleteAsync(INDEX);
                }
                if(await SearchClient.DataSources.ExistsAsync(DATA_SOUCE))
                {
                    Logger.LogInformation("{0}", "Deleting data source...\n");
                    await SearchClient.DataSources.DeleteAsync(DATA_SOUCE);
