@@ -327,6 +328,12 @@ build_shm:
 			pod_builder, &obs_pw->video_info,
 			obs_pw->format_info.array[i].spa_format, NULL, 0);
 	}
+	// blog(LOG_DEBUG, "[pipewire] Built format params [%u]", params_count);
+	// for (unsigned int i = 0; i < params_count; i++) {
+	// 	if (params[i] == NULL)
+	// 		continue;
+	// 	spa_debug_pod(0, NULL, params[i]);
+	// }
 	*param_list = params;
 	*n_params = params_count;
 	return true;
@@ -434,7 +441,7 @@ static void renegotiate_format(void *data, uint64_t expirations)
 
 	pw_thread_loop_lock(obs_pw->thread_loop);
 
-	uint8_t params_buffer[2048];
+	uint8_t params_buffer[4096];
