```php
<?php
/**
 * @psalm-template T
 */
trait AggregateTrait {
    /**
     * @psalm-readonly
     * @var T
     */
    private $value;
    /**
     * @psalm-return T
     */
    public function getValue()
    {
        return $this->value;
    }
}

/**
 * @psalm-template T
 */
trait AnotherTrait {
    /**
     * @psalm-return T
     */
    abstract public function getValue();
}

/**
 * @psalm-template T
 */ 
class Test {
    /**
     * @template-use AggregateTrait<T>
     */
    use AggregateTrait; 
    /**
     * @template-use AnotherTrait<T>
     */
    use AnotherTrait;

    /**
     * @psalm-param T $value
     */
    public function __construct($value) {
        $this->value = $value;
    }
}
```

```
