<?php
/** @psalm-template T */
trait AggregateTrait {
    /** @var T */
    private $value;

    /** @psalm-return T */
    public function getValue() {
        return $this->value;
    }
}

/** @psalm-template T */
trait AnotherTrait {
    /** @psalm-return T */
    abstract public function getValue();
}

class Test {
    /** @template-use AggregateTrait<int> */
    use AggregateTrait; 

    /** @template-use AnotherTrait<int> */
    use AnotherTrait;

    public function __construct() {
        $this->value = 123;
    }
}
```

