```php
<?php

interface ClassMetadata
{
}

interface ORMClassMetadata extends ClassMetadata
{
    public function foo(): void;
}

interface ObjectManager
{
    /**
     * @psalm-param class-string $className
     */
    public function getClassMetadata($className): ClassMetadata;
}

/**
 * @method ORMClassMetadata getClassMetadata(string $className)
 */
interface EntityManagerInterface extends ObjectManager
{
}


function (EntityManagerInterface $em): void
{
    $em->getClassMetadata(stdClass::class)->foo();
    $em->getClassMetadata('hello');
};
```

