<?php

/**
 * @template T
 * @psalm-immutable
 */
final class ListCollection
{
    /**
     * @var list<T>
     */
    private array $items;

    /**
     * @param T[] $items
     */
    public function __construct(array $items)
    {
        $this->items = array_values($items);
    }

    /**
     * @template R
     *
     * @param Closure(T):R $closure
     *
     * @return ListCollection<R>
     */
    public function map(Closure $closure): ListCollection
    {
        return new ListCollection(array_map($closure, $this->items));
    }

    /**
     * @param Closure(T):bool $closure
     *
     * @return ListCollection<T>
     */
    public function filter(Closure $closure): ListCollection
    {
        return new ListCollection(array_filter($this->items, $closure));
    }
}
