<?php

class Item {
  private int $i = 0;
	public function mutate(): void {
     $this->i++;
    }
  public function get(): int
  {
    return $this->i;
  }
}

/** 
 * @psalm-immutable
 */
class Immutable {
  private $item;

  public function __construct(Item $item)
  {
    $this->item = $item;
  }

  public function get(): int
  {
    return $this->item->get();
  }
}
$item = new Item();
$immutable = new Immutable($item);
$memory = $immutable->get();
$item->mutate();
if ($immutable->get() !== $memory) {
  // will be true
}
```

