```php
<?php

class Item {
  private int $i = 0;
	public function mutate(): void {
     $this->i++;
    }
  public function get(): int
  {
    return $this->i;
  }
}

/** 
 * @psalm-immutable
 */
class Immutable {  
  public function get(Item $item): int
  {
    return $item->get();
  }
}
$item = new Item();
$immutable = new Immutable();
$memory = $immutable->get($item);
$item->mutate();
if ($immutable->get($item) !== $memory) {
  // will be true, and it's fine
}
```

