```php
<?php

namespace iterable;

use Iterator;


function to_iterator(iterable $input): Iterator
{
    if (\is_array($input)) {
        return new \ArrayIterator($input);
    } elseif ($input instanceof Iterator) {
        return $input;
    } else {
        /** @var \Traversable $input */
        return new \IteratorIterator($input);
    }
}

/**
 * @param iterable $of
 * @param callable $reduceWith
 * @return mixed
 *
 * @template Key
 * @template Element
 *
 * @psalm-param iterable<Key, Element> $of
 * @psalm-param callable(Element, Element): Element $reduceWith
 * @psalm-return ?Element
 */
function reduce_values(iterable $of, callable $reduceWith)
{
    $iterator = namespace\to_iterator($of);
    $iterator->rewind();
    if ($iterator->valid()) {
        $aggregate = $iterator->current();
        for ($iterator->next(); $iterator->valid(); $iterator->next()) {
            $aggregate = $reduceWith($aggregate, $iterator->current());
        }
        return $aggregate;
    } else {
        return null;
    }
}
```

