<?php

class Codabar
{
    private const ENCODING = [
        '101010011', '101011001', '101001011', '110010101',
        '101101001', '110101001', '100101011', '100101101',
        '100110101', '110100101', '101001101', '101100101',
        '1101011011', '1101101011', '1101101101', '1011011011',
        '1011001001', '1010010011', '1001001011', '1010011001'
    ];

    private const INTERCHARACTER = '0';
    private const ALPHABET = '0123456789-$:/.+';

    public static function encode(string $data): string
    {
        $ret = '';

        $ret .= self::ENCODING[16] . self::INTERCHARACTER;
        $len = strlen($data);
        for ($i = 0; $i < $len; $i++) {
            $index = strpos(self::ALPHABET, $data[$i]);
            if (false === $index) {
                throw new InvalidArgumentException('Invalid character "' . $data[$i] . '" at position ' . $i);
            }
            $ret .= self::ENCODING[$index] . self::INTERCHARACTER;
        }
        $ret .= self::ALPHABET[16];
        return $ret;
    }
}
```

