return (
    <Screen py={0} px={0} scroll>
      <ConfirmModal
        visible={showSuccess}
        title="Guardado"
        description="Los datos fueron actualizados exitosamente"
        toggle={() => setShowSuccess(false)}
      />
      <Box
        bg="primary"
        borderBottomLeftRadius={4}
        borderBottomRightRadius={4}
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
        py={5}
      >
        <Photo
          value={watch('picture')}
          onChange={async data => {
            const { path } = await client({
              url: 'uploadImage',
              data,
              contentType: 'multipart/form-data',
            });
            setValue('picture', path);
          }}
        />
        <Text color="white.2" family="bold" fontSize={6}>
          {`${user.firstName || ''} ${user.lastName || ''}`}
        </Text>
        <Text color="white.2" fontSize={2}>
          {user.purchaseTotalCount} compras
        </Text>
      </Box>
      <Box flexDirection="column" px={5} py={6}>
        <Field
          errors={errors}
          label="Nombres"
          name="firstName"
          value={watch('firstName')}
          renderInput={(value, name) => (
            <Input
              family="bold"
              fontSize={3}
              color="primary"
              value={value}
              onChangeText={text => setValue(name, text)}
              onBlur={e => triggerValidation({ name, value: e.nativeEvent.text })}
              borderBottomWidth={1}
              borderBottomColor="primary"
            />
          )}
        />
        <Field
          errors={errors}
          label="Apellidos"
          name="lastName"
          value={watch('lastName')}
          renderInput={(value, name) => (
            <Input
              family="bold"
              fontSize={3}
              color="primary"
              value={value}
              onChangeText={text => setValue(name, text)}
              onBlur={e => triggerValidation({ name, value: e.nativeEvent.text })}
              borderBottomWidth={1}
              borderBottomColor="primary"
            />
          )}
        />
        <Field
          errors={errors}
          label="Correo"
          value={watch('email')}
          name="email"
          renderInput={
            user.email
              ? null
              : (value, name) => (
                  <Input
                    family="bold"
                    fontSize={3}
                    color="primary"
                    value={value}
                    onChangeText={text => setValue(name, text)}
                    onBlur={e => triggerValidation({ name, value: e.nativeEvent.text })}
                    borderBottomWidth={1}
                    borderBottomColor="primary"
                  />
                )
          }
        />
        <Field
          errors={errors}
          label="NÃºmero de telÃ©fono"
          value={watch('phone')}
          name="phone"
          renderInput={(value, name) => (
            <Input
              family="bold"
              fontSize={3}
              color="primary"
              value={value}
              onChangeText={text => setValue(name, text)}
              onBlur={e => triggerValidation({ name, value: e.nativeEvent.text })}
              borderBottomWidth={1}
              borderBottomColor="primary"
              keyboardType="phone-pad"
            />
          )}
        />

        <Field
          errors={errors}
          label="Fecha de Nacimiento"
          name="birthday"
          value={watch('birthday')}
          renderInput={(value, name) => (
            <DatePicker
              placeholder="Seleccione"
              value={value}
              onChange={text => {
                setValue(name, text);
                triggerValidation({ name, value: text });
              }}
            />
          )}
        />

        <Button
          title="Actualizar"
          mt={2}
          alignSelf="center"
          loading={formState.isSubmitting}
          disabled={formState.isSubmitting}
          onPress={handleSubmit(onSubmit)}
        />
      </Box>
    </Screen>
  );
```
