type SessionSecret struct {
  sessionKey []byte
  block cipher.Block
}
type Session struct {
  Sid     []byte    `json:"-"`
  User    string    `json:"user"`
  Remote  string    `json:"remote"`
  Version string    `json:"version"`
  Time    time.Time `json:"time"`
}
func NewSessionSecret() *SessionSecret {
  ss := &SessionSecret{}
  ss.sessionKey = make([]byte, 16)
  var err error
  if _, err = rand.Read(ss.sessionKey); err != nil { panic(err) }
  if ss.block, err = aes.NewCipher(ss.csrfKey); err != nil { panic(err) }
  return ss
}
func (ss *SessionSecret) Encrypt(session *Session) string {
  b := &bytes.Buffer{}
  {
    d := &bytes.Buffer{}
    enc := json.NewEncoder(d)
    if err := enc.Encode(session); err != nil { panic(err) }
    data := d.Bytes()
    if err := binary.Write(b, binary.LittleEndian, uint32(len(data) - 4)); err != nil { panic(err) }
    if _, err := b.Write(data); err != nil { panic(err) }
    l := len(b.Bytes())
    r := l % 64
    if r != 0 {
      pad := 64 - r
      for i := 0; i < pad; i++ {
        b.WriteByte(0)
      }
    }
  }
  text := b.Bytes()

  iv := make([]byte, 16)
  if _, err := rand.Read(iv); err != nil { panic(err) }

  cfb := cipher.NewCFBEncrypter(ss.block, iv)
  cfb.XORKeyStream(text, text)

  m := &bytes.Buffer{}
  if _, err := m.Write(iv); err != nil { panic(err) }
  if _, err := m.Write(text); err != nil { panic(err) }

  message := m.Bytes()
  session.Sid = message

  return base64.URLEncoding.EncodeToString(message)
}

func (ss *SessionSecret) Decrypt(cookie string) (*Session, error) {
  if message, err := base64.URLEncoding.DecodeString(cookie); err == nil {
    if len(message) > 2 * aes.BlockSize {
      iv := message[:aes.BlockSize]
      text := message[aes.BlockSize:]
      cfb := cipher.NewCFBDecrypter(ss.block, iv)
      cfb.XORKeyStream(text, text)

      rd := bytes.NewBuffer(text)

      if len(text) > 4 {
        datalen := len(text) - 4
        var msglen uint32
        if err := binary.Read(rd, binary.LittleEndian, &msglen); err == nil && msglen < uint32(datalen) {
          rd.Truncate(int(msglen) + 4)
          session := &Session{}
          dec := json.NewDecoder(rd)
          if err := dec.Decode(&session); err == nil {
            session.Sid = message
            if session.Version == Version {
              /* check remote and timestamp here as well? */
              return session, nil
            }
          }
        }
      }
    }
  }
  return nil, errInvalidSession
}
