from asgiref.sync import async_to_sync
from channels.generic.websocket import AsyncJsonWebsocketConsumer
from channels.layers import get_channel_layer
from raven.contrib.django.models import get_client


_client = get_client()


BRAND_GROUP = 'BRAND-{id}-GROUP'

WS_VISIT_COUNT_SLUG = 'visit-count'

WS_SLUG_LIST = [
    WS_VISIT_COUNT_SLUG,
]


@_client.capture_exceptions
def send_payload(group, **kwargs):
    channel_layer = get_channel_layer()
    async_to_sync(channel_layer.group_send)(group, {
        'type': 'general.payload',
        'content': kwargs,
    })


class Consumer(AsyncJsonWebsocketConsumer):
    @_client.capture_exceptions
    async def connect(self):
        slug = self.scope['url_route']['kwargs']['slug']
        user = self.scope['user']

        if slug in WS_SLUG_LIST:
            if user.is_brand_manager:
                await self.channel_layer.group_add(
                    BRAND_GROUP.format(id=user.brand.id),
                    self.channel_name
                )

            await self.accept()
        else:
            await self.close()

    @_client.capture_exceptions
    async def receive_json(self, content):
        slug = self.scope['url_route']['kwargs']['slug']
        user = self.scope['user']

        if slug in WS_SLUG_LIST:
            if user.is_brand_manager:
                await self.channel_layer.group_send(
                    BRAND_GROUP.format(id=user.brand.id),
                    {
                        'type': 'general.payload',
                        'content': content,
                    },
                )

    @_client.capture_exceptions
    async def disconnect(self, code):
        slug = self.scope['url_route']['kwargs']['slug']
        user = self.scope['user']

        if slug in WS_SLUG_LIST:
            if user.is_brand_manager:
                await self.channel_layer.group_discard(
                    BRAND_GROUP.format(id=user.brand.id),
                    self.channel_name
                )

    # Handler
    @_client.capture_exceptions
    async def general_payload(self, event):
        await self.send_json(event['content'])
