    {
        $result = TableA::find()->with('table_b')->all();
        foreach ($result as $v) {
            var_dump($v->table_b);
        }
    }

}

class TableA extends ActiveRecord
{
    /**
     * @inheritdoc
     */
    public static function tableName()
    {
        return '{{%table_a}}';
    }

    public function getTable_b()
    {
        return $this->hasOne(TableB::className(), ['name' => 'name']);
    }
}

class TableB extends ActiveRecord
{
    /**
     * @inheritdoc
     */
    public static function tableName()
    {
        return '{{%table_b}}';
    }
}
