            ->andWhere(['b.type' => Item::TYPE_ROLE]);
        $roles = $this->getDefaultRoleInstances();
        $rows = $this->db->cache(function () use ($query) {
            return $query->createCommand($this->db)->queryAll();
        });

        foreach ($rows as $row) {
            $roles[$row['name']] = $this->populateItem($row);
        }

